mapper.select:
SELECT ID, NAME, DOMAIN, DESCRIPTION, DATE_CREATED AS dateCreated, DATE_LAST_MODIFIED AS dateLastModified
FROM WS_SYSTEM
WHERE ID = #{id}

mapper.update:
UPDATE WS_SYSTEM
SET NAME = #{name}, DOMAIN = #{domain}, DESCRIPTION = #{description}, DATE_LAST_MODIFIED = #{dateLastModified}
WHERE ID = #{id}
